mapper.select:
SELECT DISTINCT e.evc_id AS evc_id ,e.evc_area AS evc_area ,e.evc_address AS evc_address ,e.evc_name AS evc_name ,e.evc_lat AS evc_lat ,e.evc_long AS evc_long ,c.charger_opsmall AS charger_opsmall FROM evc e JOIN evc_cg c ON e.evc_id = c.evc_id
WHERE 1 = 1
WHERE charger_type = #{property_type}
WHERE evc_area = #{evc_area}
WHERE charger_opbig = #{charger_opbig}
WHERE charger_opsmall like '%${searchText}%'
WHERE charger_type = #{charger_type} AND evc_area = #{evc_area}
WHERE charger_type = #{charger_type} AND charger_opbig = #{charger_opbig}
WHERE charger_type = #{charger_type} AND charger_opsmall like '%${searchText}%'
WHERE evc_area = #{evc_area} AND charger_opbig = #{charger_opbig}
WHERE evc_area = #{evc_area} AND charger_opsmall like '%${searchText}%'
WHERE charger_opbig = #{charger_opbig} AND charger_opsmall like '%${searchText}%'
WHERE charger_type = #{charger_type} AND evc_area = #{evc_area} AND charger_opbig = #{charger_opbig}
WHERE charger_type = #{charger_type} AND evc_area = #{evc_area} AND charger_opsmall like '%${searchText}%'
WHERE charger_type = #{charger_type} AND charger_opbig = #{charger_opbig} AND charger_opsmall like '%${searchText}%'
WHERE charger_opbig = #{charger_opbig} AND evc_area = #{evc_area} AND charger_opsmall like '%${searchText}%'
WHERE charger_type = #{charger_type} AND evc_area = #{evc_area} AND charger_opbig = #{charger_opbig} AND charger_opsmall like '%${searchText}%'
limit 300
