mapper.select:
SELECT e.evc_id as evc_id, e.evc_name as evc_name, e.evc_lat as evc_lat, e.evc_long as evc_long, c.charger_mechine as charger_mechine, c.charger_type as charger_type FROM evc e JOIN evc_cg c ON e.evc_id = c.evc_id WHERE 1=1
AND c.charger_type = #{type}
AND e.evc_area LIKE CONCAT('%', #{area}, '%')
AND c.charger_opbig = #{charger_opbig}
AND evc_name LIKE CONCAT('%', #{nameDetail}, '%')
AND charger_opsmall LIKE CONCAT('%', #{nameDetail}, '%')
LIMIT 1000;
