mapper.select:
SELECT *
FROM Festival
WHERE


		DATE(festival_startdate) <= #{selectDate} AND DATE(festival_enddate) >= #{selectDate}


		DATE(festival_startdate) <= NOW() AND DATE(festival_enddate) >= NOW()



	and festival_area = #{area}

SELECT COUNT(*)
FROM Festival
WHERE 


		DATE(festival_startdate) <= #{selectDate} AND DATE(festival_enddate) >= #{selectDate}


		DATE(festival_startdate) <= NOW() AND DATE(festival_enddate) >= NOW()



	AND festival_area = #{area}

mapper.update:
UPDATE Festival
SET festival_viewcount = festival_viewcount + 1
WHERE festival_id = #{festival_id};
