mapper.insert:
INSERT INTO product_sale ( sale_cate, sale_name, sale_descript, sale_price, sale_regdate, member_id, sale_area
) VALUES ( #{sale_cate}, #{sale_name}, #{sale_descript}, #{sale_price}, now(), #{member_id}, #{sale_area}
)

mapper.select:
SELECT ps.sale_id, ps.sale_cate, ps.sale_name, ps.sale_status, ps.sale_price, MAX(pi.productimg_url) AS productimg_url, MAX(pi.productimg_alias) AS productimg_alias, COUNT(DISTINCT fp.member_id) AS favorite_count FROM product_sale ps LEFT OUTER JOIN product_image pi ON ps.sale_id = pi.sale_id LEFT OUTER JOIN favorite_product fp ON ps.sale_id = fp.sale_id
where ps.sale_area = #{area}
GROUP BY ps.sale_id, ps.sale_cate, ps.sale_name, ps.sale_status, ps.sale_price
